// Following code has been commented with appropriate comments for your reference.
import React, { useState } from 'react';

// Function component for giving reviews
function GiveReviews({onComplete, onClose}) {

    // State variables using useState hook
  const [showForm, setShowForm] = useState(false);
  const [submittedMessage, setSubmittedMessage] = useState('');
  const [showWarning, setShowWarning] = useState(false);

  const [formData, setFormData] = useState({
    name: '',
    review: '',
    rating: 0
  });

  const handleRatingChange = (rating) => {
    setFormData(prev => ({ ...prev, rating }));
  };

  // Function to handle button click event
  const handleButtonClick = () => {
    setShowForm(true);
  };

  // Function to handle form input changes
  const handleChange = (e) => {
    // Update the form data based on user input
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // Function to handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    setSubmittedMessage(formData);
    setFormData({
      name: '',
      review: '',
      rating: 0
    });

    // Check if all required fields are filled before submission
    if (formData.name && formData.review && formData.rating > 0) {
      setShowWarning(false);
      onComplete(formData);
    } else {
      setShowWarning(true);
    }
  };

  const renderStar = (rating) => {
    const starClasses = `star ${formData.rating >= rating ? 'filled' : ''} ${formData.rating === rating ? 'clicked' : ''}`;  
    return (  <span key={rating} className={starClasses} 
              onClick={() => handleRatingChange(rating)} >
                ⭐️
              </span>
      );
  };
  

  return (
    <div className="feedback-overlay">
      Hello World!!
      <div className="feedback-modal">
        <form onSubmit={handleSubmit}>
          <h2>Give Your Feedback</h2>
          {/* Display warning message if not all fields are filled */}
          {showWarning && <p className="warning">Please fill out all fields.</p>}
          <div>
            <label htmlFor="name">Name:</label>
            <input type="text" id="name" name="name" value={formData.name} onChange={handleChange} />
          </div>
          <div>
            <label htmlFor="review">Review:</label>
            <textarea id="review" name="review" value={formData.review} onChange={handleChange}> </textarea>
          </div>

          {/* rating */}
          <div>
            <label htmlfor="rating">Rating:
            <span> {[1,2,3,4,5].map((rating) => renderStar(rating))} </span> </label>
          </div>

          {/* Submit button for form submission */}
          <div className="button-group">
            <button type="submit">Submit</button>
            <button type="button" onClick={onClose}>Close</button>
          </div>  

        </form>
      </div>
    </div>
  );
}
export default GiveReviews;
