import React, { useState, useEffect } from 'react'; //Import React library for UI components
import { Link, useNavigate } from "react-router-dom"; // import Link component for navigation using react-router-dom
import "./styles-login.css"; //import css styles
import "./styles-login_responsive.css"; //import responsive css styles
import { API_URL } from '../../config';

const Login = () => {

  // State variables for email and password
  const [password, setPassword] = useState("");
  const [email, setEmail] = useState('');

  // Get navigation function from react-router-dom
  const navigate = useNavigate();

  // Check if user is already authenticated, then redirect to home page
  useEffect(() => {
    if (sessionStorage.getItem("auth-token"))  navigate("/");
  }, []);

  // Function to handle login form submission
  const login = async (e) => {
    e.preventDefault();

    // Send a POST request to the login API endpoint
    const res = await fetch(`${API_URL}/api/auth/login`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        email: email,
        password: password,
      }),
    });

    // Parse the response JSON
    const json = await res.json();
    if (json.authtoken) {
      // If authentication token is received, store it in session storage
      sessionStorage.setItem('auth-token', json.authtoken);
      sessionStorage.setItem('email', email);

      // Redirect to home page and reload the window
      navigate('/');
      window.location.reload();
    } else {

      // Handle errors if authentication fails
      if (json.errors) {
        for (const error of json.errors) {
          alert(error.msg);
        }
      } else {
        alert(json.error);
      }
    }
  };




return (
<section className="hero-section">

  <div className="signin-container">
  <div className="signin-header">
    <h1> Sign In </h1>
    <span> A new user? <Link to="/signup"> Sign up here</Link> </span>
  </div>
  
  <form onSubmit={login}> 
    <div className="form-group">
      <div className="column">
          <label htmlfor = "email" > Email </label><br/>
          <input value={email} onChange={(e)=> setEmail(e.target.value)}
            type="text" id="email" name="email" placeholder="Enter your email" required minLength="10" pattern="^[a-zA-Z0-9.!#$%&â€™*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:\.[a-zA-Z0-9-]+)*$/"/> <br/><br/> 

          <label htmlfor ="password" > Password </label><br/>
          <input value={password} onChange={(e)=> setPassword(e.target.value)}
          type="password" id="password" name="password" placeholder="Enter your password" required/> <br/><br/>
           {showerr && <div className="err" style={{ color: 'red' }}>{showerr} </div> }

      </div>
    </div>

      <div className="button-container">
          <button type="submit">Login</button>
          <button type="reset">Reset</button>
      </div>
  </form>


  </div>
</section>


 );
}

export default Login
