import { useEffect, useState } from 'react';
import Popup from 'reactjs-popup';
import { useNavigate } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';

import './reviewform.css';
/*import GiveReviews from './GiveReviews';*/

const Review = () => {
  const navigate = useNavigate();
  const user = useSelector((state) => state.user.user);
  const [reviewData, setReviewData] = useState({});

  const reportData = [
    {
      serialNumber: 1,
      doctorName: 'Dr. Sara Bell',
      doctorSpeciality: 'Dermatology',
      charges: '$88',
      reportUrl: 'https://example-site.com/report-1',
    },
    {
      serialNumber: 2,
      doctorName: 'Dr. John Hash',
      doctorSpeciality: 'Cardiology',
      charges: '$54',
      reportUrl: 'https://example-site.com/report-2',
    },

  ];

  const handleGiveReview = (serialNumber) => {
    setReviewData((prevReviewData) => ({
      ...prevReviewData,
      [serialNumber]: ''
    }));
  };

  const handleReviewSubmit = (serialNumber, review) => {
    setReviewData((prevReviewData) => ({
      ...prevReviewData,
      [serialNumber]: review
    }));
  };

  useEffect(() => {
      if (!user) {
          navigate("/login");
      }
  }, [])

