    <div >

  <table>
    <tr>
      <th>S. No.</th>
      <th>Doctor name</th>
      <th>Doctor specialty</th>
      <th>Provide review</th>
      <th>Review given</th>
    </tr>
    <tr>
      <td>1</td>
      <td>Dr. Smith</td>
      <td>Cardiology</td>
      <td><button className="btn-review">Give Review</button></td>
      <td></td>
    </tr>
    <tr>
      <td>2</td>
      <td>Dr. Jackson</td>
      <td>Neurology</td>
      <td><button className="btn-review">Give Review</button></td>
      <td></td>

    </tr>
    <tr> 
      <td>3</td>
      <td>Dr. Johnson</td>
      <td>Neurology</td>
      <td><button className="btn-review">Give Review</button></td>
      <td></td>
    </tr>
  </table>
 

</div>
        

        </div></> );
}

export default Review;
