import { useEffect, useState } from 'react';

import './reviewform.css';
import GiveReviews from './reviewform';

const Review = () => {

 const [reviewData, setReviewData] = useState({});

  
 const renderStar = (rating) => {
    const starClasses = `star ${formData.rating >= rating ? 'filled' : ''} ${formData.rating === rating ? 'clicked' : ''}`;  
    return (  <span key={rating} className={starClasses} 
              onClick={() => handleRatingChange(rating)} >
                ⭐️
              </span>
      );
  };
  

return ( <>
<div >
  <div className="reviews-container">
  <h1> Reviews <br/></h1> 

  <table className="review-table">
    <tr>
      <th>S/No.</th>
      <th>Doctor name</th>
      <th>Doctor specialty</th>
      <th>Provide review</th>
      <th>Review given</th>
    </tr>
    <tr>
      <td>1</td>
      <td>Dr. Zela Lee</td>
      <td>General Physcian</td>
      <td><button className="review-btn">Give Review</button></td>
      <td></td>
    </tr>
    <tr>
      <td>2</td>
      <td>Dr. Williams Huang</td>
      <td>Dentist</td>
      <td><button className="review-btn">Give Review</button></td>
      <td></td>

    </tr>
    <tr> 
      <td>3</td>
      <td>Dr. Smith Johnson</td>
      <td>Cardiology</td>
      <td><button className="review-btn">Give Review</button></td>
      <td></td>
    </tr>
  </table>

 </div> </div></> 
 
);
}

export default Review;
