import React, {useEffect} from 'react';
import './report.css';
import { useNavigate } from 'react-router-dom';
import { useState, useDispatch } from 'react-redux';


const Reports = () => {
    const dispatch = useDispatch();
    const [user,setUser] = useState("");
    const navigate = useNavigate();


// Get stored data from sessionStorage, if user has login successfully
 useEffect( ()=> {
    
    const storedName = sessionStorage.getItem("name");

    //if not successfully login, navigate to /login endpoint
    (storedName) ? setUser(storedName) : ("/login");
 }, [] );


 //data obtained from future API 
const reportData = [
    {   id: 1,
        doctorName: 'Dr. Zela Lee',
        doctorSpeciality: 'General Physician',
        charges: '77.00',
        downloadUrl: 'report1.pdf',
        reportUrl: '/reports/report1.pdf',
    },
    {   id: 2,
        doctorName: 'Dr. Williams Huang',
        doctorSpeciality: 'Dentist',
        charges: '122.10',
        downloadUrl: 'report2.pdf',
        reportUrl: '/reports/report2.pdf',
    },
 ];
    
    return (
        <div className="reports-container">
        <h1>Reports</h1>
        <table className="report-table">
            <thead>
            <tr>
                <th>Serial Number </th>
                <th>Doctor Name</th>
                <th>Doctor Speciality</th>
                <th>Charges</th>
                <th>View Report</th>
                <th>Download Report</th>
            </tr>
            </thead>
            <tbody>
            {reportData.map((report, index) => (
                <tr key={index}>
                <td>{report.id}</td>
                <td>{report.doctorName}</td>
                <td>{report.doctorSpeciality}</td>
                <td>{report.charges}</td>
                <td>
                <a target='_blank' href={report.reportUrl} className="btn-review" rel="noreferrer">
                        View Report
                    </a>
                </td>
                <td>
                <a target='_blank' href={report.downloadUrl} download className="btn-review" rel="noreferrer">
                        Download Report
                    </a>
                </td>
                </tr>
            ))}
            </tbody>
        </table>
        </div>
    );
};

export default Reports;
