import React, { useState } from 'react'

const AppointmentFormIC = ({ doctorName, doctorSpeciality, onSubmit }) => {
    const [name, setName] = useState('');
    const [phoneNumber, setPhoneNumber] = useState('');
    const [appDate, setAppDate] = useState('');
    const [timeSlot, setTimeSlot] = useState(null);
  
    const today = new Date().toISOString().split('T')[0];
    const maxDate = new Date()
    maxDate.setFullYear(maxDate.getFullYear() + 1); // Add 1 year
    const maxDateString = maxDate.toISOString().split('T')[0];

    const handleFormSubmit = (e) => {
      e.preventDefault();
      onSubmit({ name, phoneNumber, appDate, timeSlot });
      setName('');
      setPhoneNumber('');
      setAppDate('');
      setTimeSlot('');
    };
  
    const onTimeSlotChange= (e) => {
      setTimeSlot(e.target.value);
    }

    const onAppDateChange = (inDate) => {
      setAppDate(inDate.target.value);
    }

    return (
      <form onSubmit={handleFormSubmit} className="appointment-form" >
        <div className="form-group">
          <label htmlFor="name">Name:</label>
          <input type="text" id="name" value={name}
            onChange={(e) => setName(e.target.value)}
            required />
        </div>

        <div className="form-group">
          <label htmlFor="phoneNumber">Phone Number:</label>
          <input type="tel" id="phoneNumber" value={phoneNumber}
            onChange={(e) => setPhoneNumber(e.target.value)}
            required />
        </div>

      <div className="form-group">
        <label htmlfor="appDate">Appointment Date </label>
        <input type="date" id="appDate" name="appDate" value={appDate} 
          min={today} max={maxDateString} onChange={onAppDateChange} 
        required/>
      </div>

      <div className="form-group">
        <label htmlfor="timeSlot"> Time Slot</label>        
        <select id="timeSlot" name="timeSlot" value={timeSlot} required onChange={onTimeSlotChange}>
          <option value=""> Select a time slot </option>
          <option value="09-10"> 09-10</option>
          <option value="10-11"> 10-11</option>
          <option value="01-12"> 11-12</option>
          <option value="14-15"> 14-15</option>
          <option value="15-16"> 15-16</option>
          <option value="16-17"> 16-17</option>
        </select>
      </div>

      <div style={{ placeItems: 'center' }}>
        <button type="submit">Book Now</button>
      </div>
      </form>
    );
  };

export default AppointmentFormIC
