import React, { useState, useEffect, useRef }  from "react";
import "./notification.css";
import Navbar from '../navbar/navbar';

/* To implement a notification window to remind user of the 
   booked/canceled appointment details
*/


const Notification = ({children}) => {

  //state variable to keep track of keeping notification window open
  const [apptWindow, setApptWindow] = useState(''); 

  const [isLoggedIn, setLogin ] = useState(false);
  const [name, setName] = useState('');
  const [phoneNum, setPhoneNum] = useState('');
  const [doctorName, setDoctorName] = useState(null); //data object
  const [doctorSpeciality, setDoctorSpeciality] = useState(null); //data object
  const [apptDate, setApptDate] = useState(null); //data object


const eventApptWindow = (e) => {
    setApptWindow(e.target.value);
}


// Do side-effects 
useEffect( ()=> {

  // Get stored data from sessionStorage & localStorage
  const storedAppData = JSON.parse(localStorage.getItem("appointments"));
  const storedName = sessionStorage.getItem(storedAppData?.name);
  const storedPhoneNum= sessionStorage.getItem(storedAppData?.phoneNum);
  const storedDoctorName = JSON.parse(localStorage.getItem(storedAppData?.doctorName));
  const storedDoctorSpeciality = JSON.parse(localStorage.getItem(storedAppData?.doctorSpeciality));

  //set the relevant state variables
  if( storedName) {
    setLogin(true);
    setName(storedName);
    setPhoneNum(storedPhoneNum);
  }

  
  if(storedAppData) {
    setDoctorName(storedDoctorName);
    setDoctorSpeciality(storedDoctorSpeciality);
  }

}, []); //empty dependency array for 1 run after initial render


return (
<div>
  {/* Render Child component */}
  {children}

 {/*Render appointment notification if user is login and apptDetail is avaliable*/} 
 { isLoggedIn && apptData && ( <>
  <div className="notification-container">
    <h1> Appointment Details</h1> <br/>

    <div className="apptDetails">
      <h4 class="lbl"> Doctor: </h4> {doctorName} 
      <h4 class="lbl"> Speciality: </h4> {doctorSpeciality} 
      <h4 class="lbl"> Name: </h4> {name}
      <h4 class="lbl"> Phone Number: </h4> {phoneNum} 
      <h4 class="lbl"> Date of Appointment: </h4> {apptDate}
      <h4 class="lbl"> Time Slot: </h4> {timeSlot} 
    </div>

  </div> </>
  )};

</div> 
 );
}

//Export Notification component for use with other parts of the application
export default Notification;
