import React, { useState, useEffect, useRef } from "react";
import { Link } from "react-router-dom";
import "./navbar.css";
import "./navbar_responsive.css";

const Navbar = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const navbarRef = useRef(null);
  const hamburgerRef = useRef(null);

  // Close menu when clicking outside
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (
        navbarRef.current &&
        !navbarRef.current.contains(event.target) &&
        !hamburgerRef.current.contains(event.target)
      ) {
        setIsMenuOpen(false);
      }
    };

    document.addEventListener("mousedown", handleClickOutside);
    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, []);

  return (
    <nav className="navbar" ref={navbarRef}>
      <div className="nav-container">
        {/* Hamburger Menu */}
        <button
          ref={hamburgerRef}
          className={`hamburger ${isMenuOpen ? "active" : ""}`}
          onClick={() => setIsMenuOpen(!isMenuOpen)}
          aria-label="Toggle navigation"
        >
          &#9776;
        </button>

        {/* Logo */}
        <div className="logo">
          <Link to="/">
            StayHealth <span className="inc">Inc</span>
            <span className="doctor-icon">
              <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 1000 1000">
                {/* SVG paths remain same */}
              </svg>
            </span>
          </Link>
        </div>

        {/* Navigation Links */}
        <ul className={`nav-links ${isMenuOpen ? "active" : ""}`}>
          <li><Link to="/services" onClick={() => setIsMenuOpen(false)}>Services</Link></li>
          <li><Link to="/appointments" onClick={() => setIsMenuOpen(false)}>Appointments</Link></li>
          <li><Link to="/blog" onClick={() => setIsMenuOpen(false)}>Blog</Link></li>
          <li><Link to="/review" onClick={() => setIsMenuOpen(false)}>Review</Link></li>
          <li><Link to="/about" onClick={() => setIsMenuOpen(false)}>About Us</Link></li>
        </ul>

        {/* Auth Buttons */}
        <div className="nav-buttons">
          <Link to="/login" className="login-btn">Login</Link>
          <Link to="/signup" className="signup-btn">Sign up</Link>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
